<style>    .vert-line-micro {        display: inline-block;        margin: 0 5px;        width: 1px;        height: 14px;        background-color: #00000066;    }</style><section class="shop-wrapp">    <div class="flex gap-10" style="min-height: 32px">        <button class="btn-gray btn-gray-text not-border micro-btn padding-btn" onclick="open_table('shops_categorys')">MAIN</button>        <button class="btn-gray btn-gray-text not-border micro-btn padding-btn" onclick="open_table('shops_undercats')">UNDER</button>        <button class="btn-gray btn-gray-text not-border micro-btn padding-btn" onclick="open_table('shops_lists')">LIST</button>    </div>    <div class="flex between gap-10 search-block">        <button class="btn-gray btn-gray-text not-border micro-btn padding-btn" onclick="apps.app_update('SHOPS')"><img style="width: 15px; height: 18px" src="/IMG/SYS/circler.svg"></button>        <button class="btn-gray btn-gray-text not-border micro-btn padding-btn" onclick="all_shops()">Все магазины</button>        <div class="text-input-wrapper" style="position: relative">            <input onblur="clear_shops_list(this)" onselect="show_shop(this)" placeholder="название магазина" oninput="find_shop(this)" type="text">            <ul id="all-shops"></ul>        </div>        <div class="vert-line-micro"></div>        <button onclick="open_table('price_cities')" class="btn-gray btn-gray-text not-border micro-btn padding-btn">Цена подкл.города</button>        <datalist id="all-shops"></datalist>    </div>    <details class="cataloger">        <summary onclick="upload_cataloger($(this).parent())" class="flex between">            <span>Каталогизатор</span>            <button class="btn-just action-btn" style="font-weight: 100; margin-left: auto; margin-right: 5px; min-width: 30px; border-radius: 3px; display: inline-block" onclick="update_cataloger_buffer()">ОЧИСТИТЬ БУФЕР</button>            <button class="btn-just action-btn" style="min-width: 30px; border-radius: 3px; display: inline-block" onclick="add_new_cat()">+</button>        </summary>        <div id="cat" class="flex column" style="gap: 2px"></div>    </details>    <details class="win-details">        <summary class="flex between"><span>Площадка</span><span></span></summary>        <div>        </div>    </details></section>