<form class="add-pay invisible">    <header>        <span>Пополнить счёт</span>        <button type="button" class="action-btn action-btn-form" onclick="hide_popap($('.add-pay'))"><?=RBS::SVG('krest')?></button>    </header>    <div class="content">        <div class="total-pay-summ flex center">            <span>Баланс Кошелька:</span>            <span><?=$cash?> P</span>        </div>        <fieldset>            <legend>Пополнение Кошелька</legend>            <input id="pay-field" name="pay-summ" type="number" min="<?=$min_add_summ?>" value="<?=$min_add_summ?>">        </fieldset>        <div style="margin-top: -20px;" class="inter-font">Минимальная сумма пополнения <?=$min_add_summ?> Р</div>        <button onclick="create_pay()" type="button" class="btn">Оплатить</button>        <div class="inter-font" style="font-size: 16px">Счет будет выставлен по <a href="#" style="color: #018536">Вашим реквизитам</a></div>        <div>Нажимая на кнопку “Оплатить” вы соглашаетись<br><a download href="/DOWNLOAD/пользовательское соглашение.docx" style="color: #018536">с пользовательским соглашением</a></div>    </div></form><script>    $(document).on('keydown', '#pay-field', function(e) {        if(e.keyCode === 13) {            create_pay();            e.preventDefault();            e.stopPropagation();        }    });    function create_pay() {        let summ = parseInt($('input[name="pay-summ"]').val());        SENDER('create_pay', {summ: summ}, function(mess) {            mess_executer(mess, function(mess) {                console.dir(mess);                location.href = mess.params;            });        });        // SENDER_AND_WAIT_RESULT('add_pay', {summ: summ}, $('.add-pay .content'), 'Ожидаем подтверждение операции...',  function(mess) {        //     open_tab('bank');        //     hide_popap($('.add-pay'));        // }, function (mess) {        //     hide_popap($('.add-pay'));        // }, 20);    }</script>