<?phpinclude_once './APPLICATIONS/SHOPS/libs/class_PAY.php';$raport = PAY::get_all_pays();$P = PROFIL::init(Access::userID());$cash = round((float)$P->get('cash'), 2);$count_pay_lids = (int)$P->get('count_pay_lids');include_CSS_once('/APPLICATIONS/SHOPS/CSS/bank.css');//wtf($raport, 1);?><section class="my-orders">    <h2 class="h2">Кошелёк</h2>    <div onclick="mobile_back_row()" class="mobire-back-row flex align-center gap-10">        <span class="svg-wrapper"><?=RBS::SVG('left_long_row')?></span>        <span>Назад</span>    </div>    <p class="ballance flex gap-10">        <span>Баланс: </span>        <span id="summ"><?=$cash?> P</span>    </p>    <? if($count_pay_lids === -1) { $count_pay_lids = 'НЕОГРАНИЧЕНО'; } ?>    <p class="ballance flex gap-10" style="margin-top: -20px">        <span>Бесплатных объявлений: </span>        <span style="color: #068638"><?=$count_pay_lids?> шт.</span>    </p>    <div class="flex gap-10 bank-btns">        <button style="padding: 12px 35px; font-size: 16px" class="btn">История операций</button>        <button id="adding" onclick="show_popap($('.add-pay'))" style="padding: 12px 35px; font-size: 16px" class="btn btn-border">Пополнение кошелька</button>    </div>    <?php if(isMobile()) { ?>        <div class="table-wrapper">            <table id="table-bank">                <tr>                    <th>Дата</th>                    <th>Операция</th>                    <th>Сумма</th>                </tr>                <?php foreach($raport['items'] as $v) {                    if($v['time_pay_ok'] !== '1970-01-01 03:00:00') {                        $dt = date('d.m.Y', strtotime($v['time_pay_ok']));                    } else {                        $dt = date('d.m.Y', strtotime($v['time_pay']));                    }                    echo '<tr><td>'.$dt.'</td><td>'.$v['operation'].'</td><td class="price">'.$v['summ'].'</td></tr>';                } ?>            </table>        </div>    <?php } else { ?>    <div class="table-wrapper">        <table id="table-bank">            <tr>                <th>Дата</th>                <th>Операция</th>                <th>Способ</th>                <th>Рубли</th>                <th>Статус</th>                <th>#</th>            </tr>            <?php foreach($raport['items'] as $v) {                if($v['status'] === '1') {                    $col = 'rgba(6, 213, 0, 0.15)';                    $status = 'Исполнено';                } elseif($v['status'] === '-1') {                    $status = 'Отклонено';                    $col = 'rgba(255, 0, 0, 0.05)';                } else {                    $status = 'В ожидании';                    $col = 'rgba(255, 247, 0, 0.1)';                }                $fil = '';                if($v['descr'] !== '-') {                    $fil = '<details><summary>Подробнее</summary><div class="micro-text" style="max-width: 200px; margin-top: 5px">'.$v['descr'].'</div></details>';                }                if($v['time_pay_ok'] !== '1970-01-01 03:00:00') {                    $dt = date('d.m.Y H:i', strtotime($v['time_pay_ok']));                } else {                    $dt = date('d.m.Y H:i', strtotime($v['time_pay']));                }                echo '<tr style="background-color: '.$col.';"><td>'.$dt.'</td><td>'.$v['operation'].'</td><td>'.$v['type_pay'].'</td><td class="price">'.$v['summ'].'</td><td>'.$status.'</td><td style="max-width: 115px">'.$fil.'</td></tr>';            } ?>        </table>    </div>    <?php } ?></section><?phpinclude_CSS_once('./APPLICATIONS/SHOPS/CSS/add_pay.css');include './APPLICATIONS/SHOPS/templates/add_pay.php';//wtf($raport, 1);?><script>    let urls = new URL(location.href);    let summ = urls.searchParams.get('summ');    if(summ !== null) {        $('#adding').click();        $('#pay-field').val(summ);        urls.searchParams.delete('summ');        history.pushState(null, null, urls);    }</script>