<?phpinclude_CSS('profil');$B = new BASKET($_SESSION['basket'] ?? '');$arr = $B->get_basket();//wtf($arr);$shops_ids = [];foreach($arr as $k=>$v) {    $shops_ids[] = $v['shop_id'];    if(empty($v['COUNT']) || (int)$v['COUNT'] === 0) {        $B->change_count($k, 0, false);        unset($arr[$k]);    }    $B->save();}$shops_ids = array_unique($shops_ids);$shops = [];foreach($shops_ids as $v) {    $shops[$v] = SHOP::get_shop($v);}foreach($arr as $k=>$v) {    $shops[$v['shop_id']]['PRODUCTS'][$k] = $v;}//wtf($shops, 1);//wtf($info, 1);//say($info);