<?phpif(Access::scanLevel() <= 0) {    Message::addError('Создавать объявления могут только зарегистрированные пользователи...');    header('Location: /');}if(isset($_GET['s'])) {    $_SESSION['shop_id'] = (int)$_GET['s'];}if(!isset($_SESSION['shop_id'])) {    Message::addError('Истекла сессия текущего пользователя. Пожалуйста для продолжения работы - авторизуйтесь.');    header('Location: /');}include_CSS('profil');include_CDN_JS('https://api-maps.yandex.ru/2.1/?lang=ru_RU&apikey='.Core::$YANDEXGEOCODER.'&suggest_apikey='.Core::$SUGGEST_GEOCODER);$apps = new APP();  // объект для работы со всеми приложениями$shops = SUBD::get_executed_rows('shops', 'owner', Access::userID());//wtf($shops);$key = array_key_first($shops);if(isset($_SESSION['shop_id']) && isset($shops[$_SESSION['shop_id']])) {    $key = (int)$_SESSION['shop_id'];    INCLUDE_CLASS('SHOPS', 'shop');    if(!SHOP::is_my_shop($key)) {        Message::addError('Запрещена работа с товаром, который вам не пренадлежит...');        header('Location: /404');    }}$P = PROFIL::init(Access::userID());$order = [];if(isset($_GET['com'], $_GET['ord'], $_GET['s']) && $_GET['com'] === 'edit') {    $_SESSION['shop_id'] = $key = (int)$_GET['s'];    INCLUDE_CLASS('SHOPS', 'shop');    INCLUDE_CLASS('SHOPS', 'props_commander');    $indexer_id = SHOP::product_code_to_indexer_ids([$_GET['s']."_".$_GET['ord']]);    $order = SHOP::get_products_list_at_indexer_ids($indexer_id, true, 'name', 'ASC', [0,2], true)[0];    $coords_props = GEONAMER::generate_scheama_array($indexer_id[0]);    foreach($coords_props as $k=>$v) {        $order['PROPS'][$k] = $v;    }    $price = $order['price'];    $descr = $order['PROPS']['descr'][0]['value'];    $addr = $order['PROPS']['place'][0]['value'];    $imgs = [];    foreach($order['PROPS']['images'] as $imgs_item) {        $imgs[] = $imgs_item['value'];    }    if($addr === '') {        $addr = $P->get('city', 'Москва');    }} else {