<?phpif(isset($_GET['tele-auth'])) {    if($row = Access::get_event($_GET['tele-auth'], ActionsList::AUTH)) {        PROFIL::AUTH_LOGIN((int)$row['params']);    }}if(Access::scanLevel() <= 0) {    header('Location: /?auth=true');    exit;}$P = PROFIL::init(Access::userID());include_once './APPLICATIONS/SHOPS/libs/class_SHOP.php';include_CSS('main');$my_subscr_count = (int)SQL_ROWS(q("SELECT COUNT(*) FROM `subscriptions` WHERE `self_id`=".Access::userID()." "))[0]['COUNT(*)'];$banners_count = (int)SQL_ROWS(q("SELECT COUNT(*) FROM `banner` WHERE `owner`=".Access::userID()." "))[0]['COUNT(*)'];$all_count_my_lots = SHOP::get_count_products(Access::userID());$all_my_shops = SHOP::get_all_my_shops(Access::userID());$shops_count = count($all_my_shops);INCLUDE_CLASS('shops', 'favorite');$favorite_count = FAVORITE::get_count_favorite_orders(Access::userID());$purchases_count = SUBD::countRows('orders', 'id_user', Access::userID());$shop_orders_count = count(SQL_ROWS(q("SELECT orders.id FROM orders LEFT JOIN shops ON shops.id=orders.shop_idWHERE shops.owner=".Access::userID()."")));foreach($all_my_shops as $k=>$v) {    if($v['active'] == 0) {        if(!$P->isset_alert_key('not_work_shop')) {            $P->add_alert(ALERT_TYPE::MESSAGE, ['text' => 'Похоже ваша торговая площадка [' . $v['name'] . '] - не работает. Пройдите по ссылке и переведите её в статус - РАБОТАЕТ', 'link' => '/profil?title=my_shops'], 'not_work_shop');        }    }}include_CDN_JS('https://api-maps.yandex.ru/2.1/?lang=ru_RU&apikey='.Core::$YANDEXGEOCODER.'&suggest_apikey='.Core::$SUGGEST_GEOCODER);