<?phpif(is_array($arr) && count($arr) >= 3) {    switch($arr[2]) {        case 'reload':            if(isset($rooms[$ROOM])) {                $anser = [                        'type'=>'answer',                        'text'=>'auto-update|all',                ];                foreach ($rooms[$ROOM] as $v) {                    if(isset($wsWorker->connections[(int)$v])) {                        $wsWorker->connections[(int)$v]->send(json_encode($anser, JSON_UNESCAPED_UNICODE));                    }                }            }            break;    }}