<?phpfunction isseter($post, $arr=[]): bool {    foreach($post as $v) {        if(!in_array($v, $arr)) {            return false;        }    }    return true;}function isset_columns($post, $columns=[]): bool|array {    $fields = [];    foreach($columns as $v) {        if(!isset($post[$v])) {            $fields[] = $v;        }    }    if(count($fields) > 0) {        return $fields;    }    return true;}function error($text, $errors=[]) {    $ans = [            'status'=>'error',            'body'=>$text,            'errors'=>$errors,    ];    echo json_encode($ans, JSON_UNESCAPED_UNICODE);    exit;}function ans($text, $params=[]) {    $ans = [            'status'=>'ok',            'body'=>$text,            'params'=>$params,    ];    echo json_encode($ans, JSON_UNESCAPED_UNICODE);    exit;}$ans = [        'status'=>'clear',        'body'=>'',        'errors'=>['Отсутствуют данные'],];$post = $_POST;