<?php$cities = SUBD::getAllDB('cities');$cities = SORT::array_sort_by_column($cities, 'name');array_unshift($cities, [        'id'=>-1,        'name'=>'-',]);$types_shops = SUBD::get_enum_from_table('shops', 'title');include_JS_once('/APPLICATIONS/SHOPS/JS/shop_creator.js');?><div class="product-creator">    <table>        <tr data-field="sys-name" data-type="string">            <td>Логотип или фирменный знак</td>            <td><label class="action-btn"><label class="logo-shop"><input id="file-input-shop" type="file" accept=".jpg, .png, .gif"></label></td>        </tr>        <tr data-field="sys-name" data-type="string">            <td>Название площадки</td>            <td colspan="2"><input data-name="name" type="text"></td>        </tr>        <tr data-field="sys-type" data-type="select">            <td>Тип площадки</td>            <td colspan="2">                <select data-name="type-shop">                    <? foreach($types_shops as $v) {                        echo '<option value="'.$v.'">'.$v.'</option>';                    } ?>                </select>            </td>        </tr>        <tr data-field="sys-caterory" data-type="select">            <td>Город</td>            <td colspan="2">                <select data-name="city">                    <? foreach($cities as $v) {                        echo '<option data-id="'.$v['id'].'" value="'.$v['name'].'">'.$v['name'].'</option>';                    } ?>                </select>            </td>        </tr>        <tr data-field="sys-address" data-type="string">            <td>Точный адрес</td>            <td colspan="2"><input data-name="address" type="text"></td>        </tr>        <tr data-type="text">            <td>Описание магазина</td>            <td>                <textarea data-name="descr" placeholder=""></textarea>            </td>        </tr>    </table></div><table class="set-to_bottom">    <tr class="gray-box">        <td></td>        <td></td>        <td style="text-align: right">            <button onclick="create_new_shop()" class="btn-just" style="padding: 2px 10px">Создать магазин</button>        </td>    </tr></table>