<?phpINCLUDE_CLASS('shops', 'cataloger');$CAT = new CATALOGER();$rows = [];$sel = $memory['sel_now'];$name_selected_column = '';$txt = "";switch($sel) {    case 'maincat':        $rows = SORT::array_sort_by_column($CAT->main_cats, 'category');        $name_selected_column = 'category';        $title = "Укажите ОСНОВНУЮ КАТЕГОРИЮ";        $txt = "В основной категории размещаются баннеры связанные общей темой, напр. ТРАНСПОРТ, в который входит легковой, грузовой, водный и т.д.";        break;    case 'undercat':        $rows = SORT::array_sort_by_column($CAT->get_all_under_cats($memory['user_sel']['main_cat']), 'under_cat');        $name_selected_column = 'under_cat';        $title = "Укажите ПОД-КАТЕГОРИЮ";        $txt = "В подкатегориях чаще всего размещается реклама более узко-специализированная, например ЛЕГКОВЫЕ АВТОМОБИЛИ и их модефикации и т.п.";        break;    case 'actionlist':        $rows = SORT::array_sort_by_column($CAT->get_all_action_list($memory['user_sel']['main_cat'], $memory['user_sel']['under_cat']), 'lists');        $name_selected_column = 'lists';        $title = "Укажите НАЗВАНИЕ СПИСКА В ПОД-КАТЕГОРИИ";        $txt = "Высокоспециализированный уровень. Чаще всего сюда будут попадать только те, кто точно знает, что они хотят найти";        break;}?><div class="flex column align-center gap-15">    <div style="font-size: 1.6em; text-align: left; align-self: flex-start; padding-right: 50px"><span><?=$title?></span></div>    <div class="text-info" style="max-width: 600px"><?=$txt?></div>    <ul class="select" style="height: 550px">        <?php        if(count($rows) > 0) {        foreach($rows as $v) {            if($v['logo'] === '-') { $v['logo'] = '20230508-154105_id-2-312764.svg'; }            ?>            <li onclick="sel_selected('<?=$sel?>', <?=$v['id']?>)" data-id="<?=$v['id']?>" class="select-item flex gap-15 action-btn">                <img width="50" height="50" src="/DOWNLOAD/<?=$v['logo']?>">                <span><?=$v[$name_selected_column]?></span>            </li>        <?php }        } else {            echo '<div style="font-size: 20px; line-height: 20px; font-weight: 600; color: red">Приносим извинения, но СПИСОК для текущей подкатегории отсутствует! Возможно Вы захотите разместить рекламу в подкатегории?..</div>';        }        ?>    </ul></div>