<?php//wtf($id_shop, 1);$arr = WORKTIME::to_array($val);?><style>    .work-time {        max-width: 100%;        box-sizing: border-box;    }    .work-time td {        padding: 1px 3px;    }    .work-time td input[type="time"] {        display: inline-block;        max-width: 80px;    }    .work-time table {        margin: 0 auto 1em;    }    .work-time td input[type="checkbox"] {        display: inline-block;        width: 25px;        height: 25px;        cursor: pointer;    }</style><div class="work-time">    <h1 style="padding-right: 50px; margin-bottom: 0.5em; margin-top: 0">Ррасписание</h1>    <table>        <tr>            <th>День</th>            <th>Раб.</th>            <th>с</th>            <th>до</th>        </tr>        <?php        foreach($arr as $k=>$v) { ?>            <tr>                <td><?=$k?></td>                <td>                    <?php                    if($v['TYPE'] === '+') {                        echo '<input onclick="check_day(this)" type="checkbox" checked="checked">';                    } else {                        echo '<input onclick="check_day(this)" type="checkbox">';                    }                    ?>                </td>                <td class="<?php if($v['TYPE'] === '-') { echo 'disabled'; } ?>"><input type="time" value="<?=$v['FROM']?>"></td>                <td class="<?php if($v['TYPE'] === '-') { echo 'disabled'; } ?>"><input type="time" value="<?=$v['TO']?>"></td>            </tr>        <?php } ?>    </table>    <button onclick="save_worktime(<?=$id_shop?>)" style="margin: 5px auto; min-width: 250px" class="btn">Сохранить</button></div>