<div class="wrapper main-header" style="margin-bottom: 20px">    <h1><?=Core::$h1;?></h1></div><section class="wrapper">    <div class="basket">        <div class="count-row flex between">            <span>Найменований товаров (<span id="names-count"><?=count($arr)?></span>)</span>            <button onclick="remove_all_from_basket()" class="action-btn red-button">Удалить всё</button>        </div>        <ul class="basket-shops-list">        <?php foreach($shops as $k=>$v) {            $shop = reset($v)['SHOP']['INFO'];//            wtf($shop, 1);            ?>            <li data-shop-id="<?=$k?>" class="shop-wrapper">                <header class="flex between align-center">                    <div class="flex align-center gap-10">                        <img width="50" height="50" src="<?='/DOWNLOAD/'.$shop['USERS_AVATAR']?>">                        <div class="user-name"><?=VALUES::getParamFromString($shop['PARAMS'], 'name', $shop['USERS_LOGIN'])?></div>                    </div>                    <button onclick="remove_shop_from_basket($(this).closest('li.shop-wrapper'))" class="action-btn red-button">Удалить товары этого магазина</button>                </header>                <div class="line-row"></div>                <table>                    <?php                    $total_price_shop = 0;                    $shop_count_products = 0;                    foreach($v as $kk=>$vv) {                        $count_pr = (int)$vv['SHOP']['COUNT'];   // количество единиц товаров                        $descr = strip_tags($vv['SHOP']['DESCR']);                        $price = (float)$vv['SHOP']['PRICE'];                        $discount = (int)$vv['SHOP']['DISCOUNT'];                        if($discount > 0) {                            $price = $price - ($price*$discount/100);                        }                        $count = (int)$vv['COUNT'];                        $shop_count_products += $count;                        if(mb_strlen($descr) > 150) {                            $descr = mb_substr($descr, 0, 150)."...";                        }                        ?>                        <tr class="basket-item" data-product-code="<?=$kk?>" data-price="<?=$price?>">                            <td width="65%">                                <div class="img-name-block" style="position: relative">                                    <img width="135" height="96" src="/IMG/img300x300/<?=$vv['SHOP']['IMG']?>">                                    <?php                                    if($discount > 0) {                                        echo '<span class="discount">-'.$discount.' %</span>';                                    }                                    ?>                                    <div class="text-name">                                        <p class="name"><?=$vv['SHOP']['PRODUCT_NAME']?></p>                                        <div class="descr"><?=$descr?></div>                                    </div>                                </div>                            </td>                            <td width="25%">                                <div class="counter flex align-center">                                    <button onclick="operation('+', this, <?=$count_pr?>)" class="action-btn"><?=RBS::SVG('plus.svg')?></button>                                    <div class="count-prods"><?=$count?></div>                                    <button onclick="operation('-', this)" class="action-btn"><?=RBS::SVG('minus.svg')?></button>                                </div>                            </td>                            <td width="10%">                                <div class="flex column between">                                    <div class="price">                                        <?=VALUES::price_format($price*$count)?> P                                    </div>                                    <button onclick="delete_item_from_basket($(this).closest('tr.basket-item'))" class="garb-btn action-btn"><?=RBS::SVG('garbage_3.svg')?></button>                                </div>                            </td>                        </tr>                    <?php                    $total_price_shop += $price * $count;                    } ?>                </table>                <footer class="flex">                    <div class="flex column">                        <div class="flex gap-15 align-center" style="margin-bottom: 10px">                            <span class="local-line">товаров (<span><?=$shop_count_products?></span>), на сумму</span>                            <span class="price"><?=VALUES::price_format($total_price_shop)?> P</span>                        </div>                        <a href="/placing?shop=<?=$k?>" class="btn action-btn" style="height: 48px">Перейти к оформлению</a>                    </div>                </footer>            </li>        <?php } ?>        </ul>    </div></section><!-- При рендаре robot ОБЯЗАТЕЛЬНО ПЕРЕДАЁМ room, type_room, params--><?php echo render('robot', [    'header_controller'=>true,  // настройка прячет phone-book в header    'room'=>'',    'type_room'=>'personal',    'params'=>[]]); ?><script>    let audio = $('audio').get(0);</script>