<?phpinclude_JS('swipper');include_CSS('swipper');include_CSS('carusel');?><div class="swiper" style="width: 500px">    <!-- Additional required wrapper -->    <div class="swiper-wrapper">        <?php foreach($slides as $v) { ?>            <div class="swiper-slide">                <?php if(isMobile()) { ?>                    <img width="200" height="200" src="/IMG/img300x300/<?=$v['VALUE']?>">                <?php } else {                    $name = $v['VALUE'];                    if(!file_exists('./RESURSES/BUFF_IMGS/'.date('Y_m_d'))) {                        mkdir('./RESURSES/BUFF_IMGS/'.date('Y_m_d'));                    }                    if(file_exists('./RESURSES/BUFF_IMGS/'.date('Y_m_d').'/'.$name)) {                        $buff_img = '/RESURSES/BUFF_IMGS/'.date('Y_m_d').'/'.$name;                    } else {                        if(file_exists(Core::$DOWNLOAD . $name)) {                            $img = new S_IMG(Core::$DOWNLOAD . $name);                            $img->bestFit(700, 400)->toFile('./RESURSES/BUFF_IMGS/' . date('Y_m_d') . '/' . $name);                            $buff_img = '/RESURSES/BUFF_IMGS/' . date('Y_m_d') . '/' . $name;                        } else {                            t("система не может найти изображение, косяк...");                        }                    }                    ?>                    <img width="200" height="200" src="<?=$buff_img?>">                <?php } ?>            </div>        <? } ?>    </div>    <!-- If we need pagination -->    <div class="swiper-pagination"></div>    <!-- If we need navigation buttons -->    <div class="swiper-button-prev"></div>    <div class="swiper-button-next"></div>    <!-- If we need scrollbar -->    <div class="swiper-scrollbar"></div></div><?php if (!isset($slider_nav)) { ?><div id="slider-nav">    <?php    $i = 0;    foreach($slides as $v) {        echo '<img data-num="'.$i++.'" class="micro-img" src="/IMG/img100x100/'.$v['VALUE'].'" width="60" height="60" style="display: inline-block; object-fit: cover">';    }    ?></div><?php } ?>