                   $(cont).find('td:nth-child(2) *').val(order_vals[keys[i]]['VALUE']);                    $(cont).attr('data-old-props-id', keys[i]);                    $(cont).attr('data-param-num', i);                    if(fixed) {                        $(cont).find('td:nth-child(3)').css('display', 'none');                    }                }            }        }    }, 10);    <?php    include_once './APPLICATIONS/SHOPS/add_fields.php';  // тут лежат поля    echo 'order_vals_edit='. json_encode($ADDED_FIELDS, JSON_UNESCAPED_UNICODE);    ?></script>