<?php f_('slider', './TEMPLATES/list_cards_product.php', 7); ?><?phpINCLUDE_CLASS('shops', 'favorite');//wtf($preview_cards_product, 1);$preview_cards_product = FAVORITE::verify_like_products($preview_cards_product, Access::userID(), 'id_shop', 'id_product');//wtf($preview_cards_product);if(Access::scanLevel() < 1) {    $basket = $_COOKIE['basket'] ?? '';} else {    $basket = $_COOKIE['basket-id-user'] ?? '';}if(isset($basket) && $basket !== '') {    $B = new BASKET($basket);    $preview_cards_product = $B->verify_in_basket_product($preview_cards_product);} else {    foreach($preview_cards_product as $k=>$v) {        $v['IN_BASKET'] = 0;        $preview_cards_product[$k] = $v;    }}?><section class="wrapper products-list">    <h2 class="h2 visually-hidden"><?php if(isset($title)) { echo $title; } else echo 'Рекомендовано для Вас'; ?></h2>    <ul>        <?php        foreach($preview_cards_product as $k=>$v) {            $shop_id = (int)$v['shop_id'];            $product_id = (int)$v['prod_id'];            (int)$discount = $v['PROPS']['discount'][0]['value'] ?? 0;            include './TEMPLATES/one-cart.php';        } ?>    </ul></section><?php f_end('slider'); ?>