<?php$PROPS = new PROPS_COMMANDER($arr['VALS']);$img = $PROPS->get_all_props_at_field_name('Изображение (фото)', true)['VALUE'];$price = $PROPS->get_all_props_at_field_name('Стоимость', true)['VALUE'];if(file_exists('./DOWNLOAD/'.$img)) {    $img = './IMG/img300x300/'.$img;} else {    $img = './IMG/SYS/img_not_exists.svg';}$local_place = $PROPS->get_all_props_at_field_name('Расположение', true)['VALUE'];if($local_place === '') {    $local_place = $arr['CITY'];}$datetime1 = new DateTime($arr['TO_ACTIVE']);$datetime2 = new DateTime(date('Y-m-d H:i:s'));$interval = $datetime1->diff($datetime2);if($datetime1 > $datetime2) {    $hours = 'Осталось ' . $interval->format("%d дн. %H ч. %I мин.");} else {    $hours = 'Время вышло';}$on_click = 'to_hi_level(this)';$text = 'Продвижение';$deff_block = '';$class = '';switch($arr['STATUS']) {    case 'archive':    case 'not_show':        $on_click = 'published(this)';        $text = 'Опубликовать';        $class = ' publish-btn ';        break;    case 'active':        $deff_block = '<button onclick="archive_item(this)" class="btn btn-red archive-btn" title="Снять с публикации">Архивировать</button>';        $class = ' higger-btn ';        break;}//wtf($arr, 1);?><li data-id="<?=$arr['ID']?>" data-id-shop="<?=$arr['SHOP_ID']?>" class="flex gap-20 one-order align-center">    <label class="check-ordr">        <input type="checkbox">    </label>    <div class="img-wrapper"><img width="300" height="180" src="<?=$img?>"></div>    <div class="flex between column center-block">        <h2><a class="h2" target="_blank" href="/<?=$arr['CAT_trans'].'/'.$arr['UNDERCAT_trans'].'/'.$arr['LIST_trans'].'/'.VALUES::translit($arr['NAME']).'?s='.$arr['SHOP_ID'].'&prod='.$arr['ID']?>"><?=$arr['NAME']?></a></h2>        <div class="price"><?=VALUES::price_format($price)?> P</div>        <ul class="micro-block">            <li class="flex align-center gap-10"><span class="svg-wrapper" style="min-width: 20px"><?=RBS::SVG('location')?></span><span><?=$local_place?></span></li>            <li class="flex align-center gap-10"><span class="svg-wrapper" style="min-width: 20px"><?=RBS::SVG('eye_green')?></span><span>Просмотров (0)</span></li>            <?php            $count = (int)$arr['COUNT'];            if($count > 0 || $count === -1) {                if($count > 0) {                ?>            <li class="flex align-center gap-10"><span class="svg-wrapper" style="min-width: 20px"><?=RBS::SVG('cubs.svg')?></span><span>Осталось (<?=$count?>)</span></li>                <?php } else { ?>                    <li class="flex align-center gap-10"><span class="svg-wrapper" style="min-width: 20px"><?=RBS::SVG('cubs.svg')?></span><span>Всегда в наличие</span></li>                <?php } ?>            <?php } ?>            <li class="flex align-center gap-10"><span class="svg-wrapper" style="min-width: 20px"><?=RBS::SVG('alarm_clock')?></span><span><?=$hours?></span></li>        </ul>    </div>    <div class="right-block">        <button onclick="edit_order(this)" class="btn">Редактировать</button>        <button onclick="<?=$on_click?>" data-status="<?=$arr['STATUS']?>" class="btn <?=$class?>"><?=$text?></button>        <?=$deff_block?>    </div></li>