<template id="loader-indicate">    <div class="circle-bar" style="transform: rotate(-90deg);">        <svg width="100%" height="100%" viewBox="0 0 42 42" class="donut">            <circle class="donut-hole" cx="21" cy="21" r="15.91549430918954" fill="red"></circle>            <circle class="donut-ring" cx="21" cy="21" r="15.91549430918954" fill="red" stroke="#301152" stroke-width="0"></circle>            <circle style="transition: 0.001s" class="donut-segment" cx="21" cy="21" r="15.91549430918954"  stroke="#509d0b" stroke-width="10" stroke-dasharray="0 100" stroke-dashoffset="0"></circle>        </svg>        <div class="timer-value">00</div>    </div></template><template id="loader-prefab">    <form id="loader-form" action="#" class="visually-hidden">        <input id="image-loader-field">    </form></template>