<div class="wrapper-input">    <input list="<?=$field_name?>" type="text" placeholder="<? if(isset($default)) { echo $default; } ?>">    <button style="pointer-events: none; cursor: pointer" class="btn"><span class="svg-wrapper"><?=RBS::SVG('row_down')?></span></button></div><datalist id="<?=$field_name?>">    <? if(isset($default)) {        echo '<option data-id="-1" value="'.$default.'">'.$default.'</option>';    } ?>    <? foreach($arr as $k=>$v) {        echo '<option data-id="'.$v['id'].'" value="'.$v[$filter_field_name].'">'.$v[$filter_field_name].'</option>';    } ?></datalist>