<?php$id = $_GET['main-cat'] ?? -1;?><div class="wrapper-input">    <select onchange="sel_main_cat(this)">        <?php        if($id === -1) {            echo '<option selected="selected" data-id="-1" value="ВСЕ КАТЕГОРИИ">ВСЕ КАТЕГОРИИ</option>';        } else {            echo '<option data-id="-1" value="ВСЕ КАТЕГОРИИ">ВСЕ КАТЕГОРИИ</option>';        }        ?>        <?php foreach($arr as $k=>$v) {            if((int)$id === (int)$v['id']) {                echo '<option selected="selected" data-id="'.$v['id'].'" value="'.$v[$filter_field_name].'">'.$v[$filter_field_name].'</option>';            } else {                echo '<option data-id="'.$v['id'].'" value="'.$v[$filter_field_name].'">'.$v[$filter_field_name].'</option>';            }        } ?>    </select>    <button style="pointer-events: none; cursor: pointer" class="btn"><span class="svg-wrapper"><?=RBS::SVG('row_down')?></span></button></div><script>    function sel_main_cat(obj) {        let id = $(obj).find('option:selected').attr('data-id');        url.searchParams.set('main-cat', id);        let new_url = 'https://'+url.hostname+url.pathname+'?'+url.searchParams;        history.pushState(null, null, new_url);        location.reload();    }</script>