<?phpclass CACHE {    public static function clear_cache() {        $directory = './logs/Cache/';        $files = scandir($directory);        $time_limit = 300; // 1 час = 3600 секунд        foreach ($files as $file) {            if ($file === '.' || $file === '..') {                continue;            }            $file_path = $directory . $file;            $file_time = filemtime($file_path);            if (time() - $file_time > $time_limit) {                unlink($file_path);            }        }        $arr = glob('./SITE/' . "*");        foreach($arr as $v) {            unlink($v);        }        unlink('./RESURSES/cataloger.txt');        $arr = glob('./RESURSES/BUFFER/' . "*");        foreach($arr as $v) {            unlink($v);        }    }    public static function add_in_cache(string $file_name, $body) {        file_put_contents('./RESURSES/BUFFER/cache_'.$file_name.".serialize", serialize($body));    }    public static function get_from_cache(string $file_name) {        if(file_exists('./RESURSES/BUFFER/cache_'.$file_name.".serialize")) {            return unserialize(file_get_contents('./RESURSES/BUFFER/cache_'.$file_name.".serialize"));        } else {            return false;        }    }}