<?phpclass CACHE {    public static function clear_cache() {        $directory = './logs/Cache/';        $files = scandir($directory);        $time_limit = 300; // 1 час = 3600 секунд        foreach ($files as $file) {            if ($file === '.' || $file === '..') {                continue;            }            $file_path = $directory . $file;            $file_time = filemtime($file_path);            if (time() - $file_time > $time_limit) {                unlink($file_path);            }        }    }}