<?phpclass CACHE {    public static function set(string $string_key, mixed $value, int $seconds = 20) {        $PR = new REDISE();        $key = md5($string_key);        $PR->set($key, serialize($value), $seconds);    }    public static function get(string $string_key) {        $PR = new REDISE();        $key = md5($string_key);        if($PR->isset($key)) {            return unserialize($PR->get($key));        }        return null;    }}