<?phpuse PHPMailer\PHPMailer\PHPMailer;use PHPMailer\PHPMailer\Exception;class MAIL {    public static function sender($e_mailAddress, $title='Test', $body='Hello world'): string {        require __DIR__.'/../vendor/autoload.php';        $mail = new PHPMailer(true);        try {            // Настройки SMTP-сервера            $mail->CharSet = "utf-8";//            $mail->SMTPDebug = 2;                               // Включить отладку SMTP            $mail->isSMTP();                                      // Указываем использование SMTP-сервера            $mail->Host = 'mail.kokonk.com';                      // Указываем адрес SMTP-сервера            $mail->SMTPAuth = true;                               // Включаем авторизацию на SMTP-сервере            $mail->Username = 'admin@kokonk.com';                 // Логин от вашей почты            $mail->Password = 'mC1sU3aT8l';                       // Пароль от вашей почты            $mail->SMTPSecure = 'tls';                            // Указываем тип шифрования            $mail->Port = 587;                                    // Указываем порт SMTP-сервера  587  (465, 993, 995-overload)            // Настройки email-сообщения            $mail->setFrom('admin@kokonk.com', 'Admin');         // Указываем отправителя            $mail->addAddress($e_mailAddress, 'To Name');                // Указываем получателя            $mail->addReplyTo('admin@kokonk.com', 'Admin');      // Указываем адрес для ответа            $mail->isHTML(true);                                       // Указываем, что сообщение в формате HTML            $mail->Subject = $title;                                           // Указываем тему сообщения            $mail->Body    = "<div>".$body."</div>";                                            // Указываем тело сообщения//            $mail->SMTPOptions = array(//                'ssl' => array(//                    'verify_peer' => false,//                    'verify_peer_name' => false,//                    'allow_self_signed' => true//                )//            );            $mail->send();            return 'Message has been sent';        } catch (Exception $e) {            return $mail->ErrorInfo;        }    }}