<?phpuse PHPMailer\PHPMailer\PHPMailer;use PHPMailer\PHPMailer\Exception;class MAIL {    public static function sender($e_mailAddress, $title='Test', $body='Hello world'): string {        require __DIR__.'/../vendor/autoload.php';        $mail = new PHPMailer(true);        try {            // Настройки SMTP-сервера            $mail->CharSet = "utf-8";//            $mail->SMTPDebug = 2;                               // Включить отладку SMTP            $mail->isSMTP();                                      // Указываем использование SMTP-сервера            $mail->Host = 'mail.rumbra.ru';                       // Указываем адрес SMTP-сервера            $mail->SMTPAuth = true;                               // Включаем авторизацию на SMTP-сервере            $mail->Username = 'admin@rumbra.ru';                  // Логин от вашей почты            $mail->Password = 'iQ6zZ8sN3lbI4dsg3j5';              // Пароль от вашей почты            $mail->SMTPSecure = 'tls';                            // Указываем тип шифрования            $mail->Port = 587;                                    // Указываем порт SMTP-сервера  587  (465, 993, 995-overload)            // Настройки email-сообщения            $mail->setFrom('admin@rumbra.ru', 'Admin');         // Указываем отправителя            $mail->addAddress($e_mailAddress, 'To Name');                // Указываем получателя            $mail->addReplyTo('admin@rumbra.ru', 'Admin');      // Указываем адрес для ответа            $mail->isHTML(true);                                       // Указываем, что сообщение в формате HTML            $mail->Subject = $title;                                           // Указываем тему сообщения            $mail->Body    = "<div>".$body."</div>";                                            // Указываем тело сообщения            $mail->send();            return 'Message has been sent';        } catch (Exception $e) {            return $mail->ErrorInfo;        }    }}