<?phpclass Message {    public static function addError($errorText) {        if(!empty($errorText)) {            $_SESSION['errors'][] = $errorText;        }   }    public static function addWarning($messageText) {        if(!empty($messageText)) {            $_SESSION['warnings'][] = $messageText;        }    }    public static function addMessage($messageText) {        if(!empty($messageText)) {            $_SESSION['messages'][] = $messageText;        }    }    public static function render() {//        if(!empty($_SESSION['messages'])) {//            wtf($_SESSION['messages']);//        }        echo '<script>';        if(!empty($_SESSION['messages'])) {            foreach($_SESSION['messages'] as $k=>$v) {                echo 'say("'.db_secur($v).'");';            }            $_SESSION['messages'] = array();        }        if(!empty($_SESSION['errors'])) {            foreach($_SESSION['errors'] as $k=>$v) {                echo 'say("'.db_secur($v).'", 3);';            }            $_SESSION['errors'] = array();        }        if(!empty($_SESSION['warnings'])) {            foreach($_SESSION['warnings'] as $k=>$v) {                echo 'say("'.db_secur($v).'", 2);';            }            $_SESSION['warnings'] = array();        }        echo '</script>';    }}