<?phpclass ROOMS {    public REDISE $RED;    public function __construct() {        $this->RED = new REDISE();    }    public function isset_room($id_room):bool {        if($this->RED->obj_isset('ROOMS', $id_room)) {            return true;        }        return false;    }    public function get_room($id_room):array {        if($this->RED->obj_isset('ROOMS', $id_room)) {            return unserialize($this->RED->obj_get('ROOMS', $id_room));        }        return [];    }    public function insert_connect_in_room($id_room, $id_connect) {        $arr = $this->get_room($id_room);        if(count($arr) > 0) {            if(!in_array($id_connect, $arr)) {                $arr[] = $id_connect;                $this->RED->obj_set('ROOMS', $id_room, serialize($arr));            }        } else {            $arr = [];            $arr[] = $id_connect;            $this->RED->obj_set('ROOMS', $id_room, serialize($arr));        }    }    public function remove_connect_from_room($id_room, $id_connect) {        $arr = $this->get_room($id_room);        if(count($arr) > 0) {            if(in_array($id_connect, $arr)) {                unset($arr[array_search($id_connect, $arr)]);                $this->RED->obj_set('ROOMS', $id_room, serialize($arr));            }        }    }    public function add_in_room($id_room, $id_connect) {    }}